--
-- Estructura de tabla para la tabla `orders`
--

CREATE TABLE `orders` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `seller_id` bigint(20) UNSIGNED DEFAULT NULL,
  `seller_order_id` bigint(20) UNSIGNED DEFAULT NULL,
  `total` decimal(10,2) NOT NULL,
  `original_total` decimal(10,2) DEFAULT NULL COMMENT 'Total original antes de descuentos',
  `subtotal_products` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Subtotal de productos',
  `iva_amount` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Monto del IVA',
  `shipping_cost` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Costo de envío',
  `total_discounts` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Total de descuentos aplicados',
  `volume_discount_savings` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Ahorros por descuentos de volumen',
  `seller_discount_savings` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Ahorros por descuentos del seller',
  `volume_discounts_applied` tinyint(1) NOT NULL DEFAULT 0 COMMENT 'Si se aplicaron descuentos por volumen',
  `free_shipping` tinyint(1) NOT NULL DEFAULT 0 COMMENT 'Si el envío es gratis',
  `free_shipping_threshold` decimal(10,2) DEFAULT NULL COMMENT 'Umbral para envío gratis aplicado',
  `pricing_breakdown` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Desglose detallado de precios' CHECK (json_valid(`pricing_breakdown`)),
  `feedback_discount_code` varchar(20) DEFAULT NULL COMMENT 'Código de descuento de feedback aplicado',
  `feedback_discount_amount` decimal(10,2) NOT NULL DEFAULT 0.00 COMMENT 'Monto del descuento de feedback',
  `feedback_discount_percentage` decimal(5,2) NOT NULL DEFAULT 0.00 COMMENT 'Porcentaje del descuento de feedback',
  `status` varchar(255) NOT NULL DEFAULT 'pending',
  `payment_id` varchar(255) DEFAULT NULL,
  `payment_method` varchar(255) DEFAULT NULL,
  `payment_status` varchar(255) DEFAULT NULL,
  `payment_details` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`payment_details`)),
  `shipping_data` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`shipping_data`)),
  `order_number` varchar(255) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `delivered_at` timestamp NULL DEFAULT NULL,
  `completed_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

